INSERT INTO `ja0gi_rsform_forms` VALUES (1,'RSformPro example','<h2>{global:formtitle}</h2>\n{error}\n<!-- Do not remove this ID, it is used to identify the page so that the pagination script can work correctly -->\n<fieldset class=\"formHorizontal formContainer\" id=\"rsform_1_page_0\">\n	<div class=\"rsform-block rsform-block-header\">\n		<div class=\"formControlLabel\">{Header:caption}</div>\n		<div class=\"formControls\">\n		<div class=\"formBody\">{Header:body}<span class=\"formValidation\">{Header:validation}</span></div>\n		<p class=\"formDescription\">{Header:description}</p>\n		</div>\n	</div>\n	<div class=\"rsform-block rsform-block-fullname\">\n		<div class=\"formControlLabel\">{FullName:caption}<strong class=\"formRequired\">(*)</strong></div>\n		<div class=\"formControls\">\n		<div class=\"formBody\">{FullName:body}<span class=\"formValidation\">{FullName:validation}</span></div>\n		<p class=\"formDescription\">{FullName:description}</p>\n		</div>\n	</div>\n	<div class=\"rsform-block rsform-block-email\">\n		<div class=\"formControlLabel\">{Email:caption}<strong class=\"formRequired\">(*)</strong></div>\n		<div class=\"formControls\">\n		<div class=\"formBody\">{Email:body}<span class=\"formValidation\">{Email:validation}</span></div>\n		<p class=\"formDescription\">{Email:description}</p>\n		</div>\n	</div>\n	<div class=\"rsform-block rsform-block-companysize\">\n		<div class=\"formControlLabel\">{CompanySize:caption}<strong class=\"formRequired\">(*)</strong></div>\n		<div class=\"formControls\">\n		<div class=\"formBody\">{CompanySize:body}<span class=\"formValidation\">{CompanySize:validation}</span></div>\n		<p class=\"formDescription\">{CompanySize:description}</p>\n		</div>\n	</div>\n	<div class=\"rsform-block rsform-block-position\">\n		<div class=\"formControlLabel\">{Position:caption}<strong class=\"formRequired\">(*)</strong></div>\n		<div class=\"formControls\">\n		<div class=\"formBody\">{Position:body}<span class=\"formValidation\">{Positi